mapper.select:
SELECT *
FROM d1_user
WHERE username = #{username} OR email = #{email}

SELECT * FROM d1_user
WHERE userKey IN

    #{key}

SELECT COUNT(*)
FROM d1_user
WHERE username = #{username}

mapper.update:
UPDATE d1_user
SET
    username = #{username},
    useralias = #{useralias},
    userIntro = #{userIntro},
    editedDate = #{editedDate}

        , email = #{email}

        , password = #{password}

        , userimg = #{userimg}

WHERE userKey = #{userKey}

UPDATE d1_user
SET visitcnt = visitcnt + 1
WHERE username = #{username}

UPDATE d1_user
SET tradecnt = tradecnt + 1
WHERE userKey = #{userKey}

UPDATE d1_user
SET state = #{state}, editedDate = #{time}
WHERE userKey = #{userKey}
